import { useState } from "react";

function Navigation(){
    const navItems =['Home','About','Contact Us','Service','Team'];
    const [selectedIndex,setSelectedIndex]=useState(-1);
    return (
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">
                <a className="navbar-brand" href="#">
                    <img src="/images/Logo.svg" alt="Infinity Logo" width={100} />
                </a>
                <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
                </button>
                <div class="collapse navbar-collapse" id="navbarNav">
                    <ul class="navbar-nav">
                        {navItems.map((items,index)=>(
                            <li key={index} className={selectedIndex === index?'nav-item active':'nav-item'} onClick={()=>{setSelectedIndex(index)}}>
                                <a class="nav-link" href="#">{items}</a>
                            </li>
                        ))}

                    </ul>
                    <div className="search-container">
                        {isInputVisible ? (
                            <div className="search-input">
                                <input type="text" placeholder="Search..." />
                                <button onClick={handleClearClick}>X</button>
                            </div>
                        ) : (
                            <div className="search-icon" onClick={handleSearchClick}>
                                &#128269;
                            </div>
                        )}
                    </div>
                </div> 

            </div>
        </nav>
    )
}

export default Navigation
