import './About.scss';
import React, { useEffect, useRef, useState } from 'react';
import CountUp from "react-countup";

const About = () => {
  const breadcrumbStyle = {
    '--bs-breadcrumb-divider': "'>"
  };

  const [startCounter, setStartCounter] = useState(false);
  const counterRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const element = counterRef.current;
      if (!element) return;

      const position = element.getBoundingClientRect();
      const windowHeight = window.innerHeight || document.documentElement.clientHeight;

      if (position.top < windowHeight * 0.75 && position.bottom >= 0) {
        setStartCounter(true);
        window.removeEventListener('scroll', handleScroll);
      }
    };

    window.addEventListener('scroll', handleScroll);
    
  }, []);

    return(
      <>
      
      <section className="counter-section">
        <div className="container">
          <div className="row">
            <div className="col-lg-6">
              <div className="counter-wrapper">
                <div className="project-counter-wrapper d-flex align-items-center">
                  <div className="project-img-container">
                    <img src="images/about/counter.webp" alt="projects" />
                  </div>
                  <div className="project-content">
                    <span className="d-block "><CountUp
                    start={0}
                    end={50}
                    duration={5}
                /></span>
                    <span className="d-flex">Projects Completed</span>
                  </div>
                   
                </div>
              </div>
            </div>
            <div className="col-lg-6">
              <h4 className="">STATS ABOUT WORK</h4>
              <h3  className="">Some Interesting Facts</h3>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Leo nec felis at pellentesque. Sed amet dolor penatibus sed at posuere justo, lacinia neque.Lorem ipsum dolor sit amet, consectetur adipiscing elit. Leo nec felis at pellentesque. Sed amet dolor penatibus sed at posuere justo, lacinia neque.</p>
              <div className="view-more-button-wrapper mt-4">
                <button type="button" className="view-more-btn btn text-uppercase">View More</button>
              </div>
            </div>
          </div>
        </div>
      </section>
      </>
    );
  };
  
  export default About;
