import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './Home.scss';

const Home = () => {
    const logoSlider = {
        dots: false,
        infinite: true,
        speed: 1000,
        slidesToShow: 5,
        slidesToScroll: 1,
        autoplay: true, // Enable autoplay
        autoplaySpeed: 0, // Set autoplay speed to 0 for continuous sliding
        cssEase: 'linear', // Set the CSS animation easing to linear
        variableWidth: true, // Allow variable slide widths for continuous sliding effect
        adaptiveHeight: true, // Allow variable slide heights for better handling of images
        pauseOnHover: false, // Disable pausing when hovering over the slider
        useCSS: true,
        
    };

    const images = [
        '/images/logoipsum-286.svg', 
        '/images/logoipsum-297.svg', 
        '/images/logoipsum-321.svg',
        '/images/logoipsum-325.svg',
        '/images/logoipsum-329.svg',
        '/images/logoipsum-330.svg',
        '/images/logoipsum-331.svg',
      ];
    
    return (
        <>
        <section className="home-banner-section">
            <div className="container">
                <div className="row align-items-center">
                    <div className="col-lg-7 order-2 order-lg-1">   
                        <h1 className="banner-title">We create <span className="text-voilet">amazing </span> digital products</h1>
                        <p className="banner-para pt-3">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Amet adipiscing nam suspendisse et lobortis. Adipiscing auctor lobortis in lorem cursus cursus eget duis massa. Diam sem volutpat turpis condimentum.</p>
                        <div className="pt-4">
                            <button type="button" className="btn text-uppercase know-more-btn rounded-pill">know more</button>
                        </div>
                    </div>
                    <div className="col-lg-5 order-1 order-lg-2">   
                        <div className="banner-img-container ">
                            <img src="/images/banner-img.png" alt="banner" className="img-fluid"/>
                        </div>
                    </div>
                </div>
            </div>
        </section>
        <div>
            <Slider {...logoSlider} className="slider-for">
                {images.map((image, index) => (
                    <div key={index}>
                        <img src={image} alt={`Slide ${index}`} />
                    </div>
                ))}
            </Slider>
        </div>
        </>
    );
};

export default Home;
